import { Handle, Position, type NodeProps } from "@xyflow/react";
import type { TaskNode } from "@/types/task";
import { getStatusColor, getStatusLabel } from "@/utils/task-utils";
import { Badge } from "@/components/ui/badge";

export const TaskNodeComponent = memo(({ data }: NodeProps<TaskNode>) => {
  const statusColor = getStatusColor(data.status);
  const statusLabel = getStatusLabel(data.status);

  return (
    <div className="relative bg-card border rounded-lg shadow-sm min-w-[200px] max-w-[300px]">
      {/* Top handle for incoming connections */}
      <Handle type="target" position={Position.Top} className="!bg-muted-foreground" />

      {/* Header with status badge */}
      <div className="flex items-center justify-between p-3 border-b bg-muted/50">
        <div className="flex items-center gap-2">
          <div className={`w-3 h-3 rounded-full ${statusColor}`} />
          <span className="text-xs font-medium">{data.id}</span>
        </div>
        <Badge variant="outline" className="text-xs">
          {data.size}
        </Badge>
      </div>

      {/* Content */}
      <div className="p-3 space-y-2">
        <p className="text-sm font-medium line-clamp-2">{data.spec}</p>

        <div className="flex items-center justify-between text-xs text-muted-foreground">
          <span>Status: {statusLabel}</span>
          <span>
            {data.attempts}/{data.maxAttempts}
          </span>
        </div>

        {data.workerModel && (
          <div className="text-xs text-muted-foreground">
            Model: {data.workerModel}
          </div>
        )}

        {data.telemetry && (
          <div className="flex gap-3 text-xs text-muted-foreground">
            <span>{data.telemetry.tokens.toLocaleString()} tokens</span>
