      const { nodes, edges } = await getLayoutedElements(tasks, edges);
      if (mounted) {
        setNodes(nodes);
        setEdges(edges);
      }
    }

    applyLayout();

    return () => {
      mounted = false;
    };
  }, [tasks, edges, setNodes, setEdges]);

