@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground;
    background: linear-gradient(135deg,
      oklch(0.145 0.02 250) 0%,
      oklch(0.125 0.015 260) 50%,
      oklch(0.145 0.02 270) 100%);
    background-attachment: fixed;
  }
}

@layer utilities {
  /* Glass effect utilities */
  .glass {
    background: oklch(from var(--card) l c h / 0.7);
    backdrop-filter: blur(12px) saturate(180%);
    border: 1px solid oklch(from var(--border) l c h / 0.3);
  }

  .glass-strong {
    background: oklch(from var(--card) l c h / 0.85);
    backdrop-filter: blur(16px) saturate(200%);
    border: 1px solid oklch(from var(--border) l c h / 0.4);
  }

  /* Glow effects */
  .glow-primary {
    box-shadow: 0 0 20px oklch(from var(--primary) l c h / 0.3),
                0 0 40px oklch(from var(--primary) l c h / 0.15);
  }

  .glow-accent {
    box-shadow: 0 0 20px oklch(from var(--accent) l c h / 0.3),
                0 0 40px oklch(from var(--accent) l c h / 0.15);
  }

  .glow-success {
    box-shadow: 0 0 20px oklch(0.7 0.15 150 / 0.3),
                0 0 40px oklch(0.7 0.15 150 / 0.15);
  }

  .glow-warning {
    box-shadow: 0 0 20px oklch(0.7 0.15 60 / 0.3),
                0 0 40px oklch(0.7 0.15 60 / 0.15);
  }

  .glow-error {
    box-shadow: 0 0 20px oklch(0.6 0.2 30 / 0.3),
                0 0 40px oklch(0.6 0.2 30 / 0.15);
  }

  /* Smooth animations */
  .animate-fade-in {
    animation: fade-in 0.3s ease-out;
  }

  .animate-slide-up {
    animation: slide-up 0.4s ease-out;
  }

  .animate-slide-down {
    animation: slide-down 0.4s ease-out;
  }

  .animate-scale-in {
    animation: scale-in 0.3s ease-out;
  }

  .animate-shimmer {
    animation: shimmer 2s infinite;
    background: linear-gradient(
      90deg,
      oklch(from var(--muted) l c h / 0.3) 0%,
      oklch(from var(--muted) l c h / 0.5) 50%,
      oklch(from var(--muted) l c h / 0.3) 100%
    );
    background-size: 200% 100%;
  }

  /* Pulse animation for status indicators */
  .animate-pulse-glow {
    animation: pulse-glow 2s ease-in-out infinite;
  }
}

@keyframes fade-in {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

@keyframes slide-up {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes slide-down {
  from {
    opacity: 0;
    transform: translateY(-20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes scale-in {
  from {
    opacity: 0;
    transform: scale(0.95);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

@keyframes shimmer {
  0% {
    background-position: -200% 0;
  }
  100% {
    background-position: 200% 0;
  }
}

@keyframes pulse-glow {
  0%, 100% {
    opacity: 1;
    filter: brightness(1);
  }
  50% {
    opacity: 0.8;
    filter: brightness(1.2);
  }
}

