 */
export function getStatusLabel(status: TaskStatus): string {
  const labelMap: Record<TaskStatus, string> = {
    queued: "Queued",
    running: "Running",
    blocked: "Blocked",
    escalated: "Escalated",
    needs_human: "Needs Human",
    failed: "Failed",
    done: "Done",
  };
  return labelMap[status];
}

