  const glowMap: Record<TaskStatus, string> = {
    queued: "",
    running: "glow-primary",
    blocked: "glow-warning",
    escalated: "glow-warning",
    needs_human: "glow-accent",
    failed: "glow-error",
    done: "glow-success",
  };
  return glowMap[status];
}

